const fileDownload = require('js-file-download')

const allVue = require.context('@/views/', true, /index.vue$/)

let SIGN_REGEXP = new RegExp('([yMdhsm])(\\1*)', 'g')
let DEFAULT_PATTERN = 'yyyy-MM-dd'
export default {
  methods: {
    menuNameToPath(name) {
      switch (name) {
        case '产品框架定义':
          return '/product-definition/pd-framework-def/main'
        case '产品销售定义':
          return '/product-definition/pd-operation-def/pd-sales-def/sales-main'
        case '产品核赔定义':
          return '/product-definition/pd-operation-def/pd-compensation-def/compensation-main'
        case '产品保全定义':
          return '/product-definition/pd-operation-def/pd-preservation-def/preservation-main'
        case '产品续期续保定义' :
          return '/product-definition/pd-operation-def/pd-renewal-def/renewal-main'
        case '产品核保定义':
          return '/product-definition/pd-operation-def/pd-underwriting-def/underwriting-main'
        case '产品承保定义':
          return '/product-definition/pd-operation-def/pd-acceptance-def/acceptance-main'
        case '产品审核':
          return '/commodity-audit/pd-audit-info/pd-audit-detail'
        case '产品发布':
          return '/commodity-release/productionRelease/main'
        case '新单测试':
          return '/product-testing/new-single-test/new-single-test-detail'
        case '续期续保测试':
          return '/product-testing/renewal-test/renewal-test-detail'
        case '保全测试':
          return '/product-testing/preservation-test/preservation-test-detail'
        case '理赔测试':
          return '/product-testing/claim-test/detail'
        case '核保测试':
          return '/product-testing/underwriting-test/underwriting-test-detail'
        case '规则测试':
          return '/product-testing/ruleTest'
        case '产品套餐内容定义':
          return '/pdPackageInfo/profuctPpMenu'
        case '产品套餐方案定义':
          return '/pdPackageInfo/profuctPpMenu'
        case '产品套餐属性定义':
          return '/pdPackageInfo/profuctPpMenu'
        case '产品套餐保费优惠定义':
          return '/pdPackageInfo/profuctPpMenu'
        case '产品套餐销售定义':
          return '/pdPackageInfo/pdPackageSaleMain'
        case '产品套餐承保定义':
          return '/pdPackageInfo/acceptanceDefinitionMain'
        case '产品套餐核保定义':
          return '/pdPackageInfo/underwritingDefinitionMain'
        default :
          return ''
      }
    },
    focusItem() {
      try {
        this.$nextTick(() => {
          let isError = document.getElementsByClassName('is-error')
          if (isError[0]) {
            let ele = isError[0].querySelector('input', 'select', 'textarea')
            if (ele) {
              ele.focus()
            } else {
              let textarea = isError[0].getElementsByTagName('textarea')[0]
              if (textarea) {
                textarea.focus()
              }
            }
          }
        })
      } catch (e) {
      }
    },
    downloadResponseFile(response, fileName) {
      fileDownload(response, fileName)
    },
    buildRequestData(data) {
      return {'params': data}
    },
    removeCacheData(key) {
      sessionStorage.removeItem(key)
    },
    cacheStrData(key, val) {
      sessionStorage.setItem(key, val)
    },
    getStrData(key) {
      return sessionStorage.getItem(key)
    },
    cacheJsonData(key, val) {
      this.cacheStrData(key, JSON.stringify(val))
    },
    getJsonData(key) {
      let str = this.getStrData(key)
      if (str) {
        return JSON.parse(str)
      } else {
        return {}
      }
    },
    cachePdData(key, val) {
      this.cacheStrData(key, JSON.stringify(val))
    },
    cacheCurrPagePdData(val) {
      let keyName = this.$route.path
      this.cacheStrData(keyName, JSON.stringify(val))
    },
    getPdData() {
      let keyName = this.$route.path
      let str = this.getStrData(keyName)
      if (str) {
        return JSON.parse(str)
      } else {
        return {}
      }
    },
    getRouteParam(key) {
      let path = this.$route.path
      let keyInLocalStorage = path + '_' + key
      if (this.$route.params[key]) {
        if (this.$route.params[key] !== localStorage.getItem(keyInLocalStorage)) {
          localStorage.setItem(keyInLocalStorage, this.$route.params[key])
        }
        return this.$route.params[key]
      } else {
        return localStorage.getItem(keyInLocalStorage)
      }
    },
    notifySuccess(msg, callback) {
      this.$notify({
        title: this.$t('notifyTitle.success'),
        message: msg,
        type: 'success',
        duration: 2000,
        onClose: function () {
          callback && callback()
        }
      })
    },
    notifyWarning(msg, callback) {
      this.$notify({
        title: this.$t('notifyTitle.warning'),
        message: msg,
        type: 'warning',
        duration: 2000,
        onClose: function () {
          callback && callback()
        }
      })
    },
    notifyError(msg, callback) {
      this.$notify({
        title: this.$t('notifyTitle.error'),
        message: msg,
        type: 'error',
        duration: 2000,
        onClose: function () {
          callback && callback()
        }
      })
    },
    notifyInfo(msg, callback) {
      this.$notify({
        title: this.$t('notifyTitle.info'),
        message: msg,
        type: 'info',
        duration: 2000,
        onClose: function () {
          callback && callback()
        }
      })
    },
    messageSuccess(msg) {
      this.$message({
        message: msg,
        type: 'success'
      })
    },
    messageError(msg) {
      this.$message({
        message: msg,
        type: 'error'
      })
    },
    messageWarning(msg) {
      this.$message({
        message: msg,
        type: 'warning'
      })
    },
    messageInfo(msg) {
      this.$message({
        message: msg
      })
    },
    confirmWarning(msg) {
      return this.$confirm(msg, '提示', {
        type: 'warning'
      })
    },
    confirmSuccess(msg) {
      return this.$confirm(msg, '提示', {
        type: 'success'
      })
    },
    confirmError(msg) {
      return this.$confirm(msg, '提示', {
        type: 'error'
      })
    },
    filterOptions(filterStr, tempOptions) {
      let targetOptions = tempOptions
      if (filterStr !== '') {  //过滤  // 1$2$0$1
        let arr = filterStr.split('$')
        let temp = {}
        if (arr[0] === '1') { //只显示
          for (let i = 1; i < arr.length; i++) {
            temp[arr[i]] = true
          }
          targetOptions = tempOptions.filter(ele => temp[ele.code])
        } else if (arr[0] === '0') { //不显示
          for (let i = 1; i < arr.length; i++) {
            temp[arr[i]] = false
          }
          targetOptions = tempOptions.filter(ele => temp[ele.code] !== false)
        }
      }
      return targetOptions
    },
    formatDate(date, pattern) {
      pattern = pattern || DEFAULT_PATTERN
      return pattern.replace(SIGN_REGEXP, function ($0) {
        switch ($0.charAt(0)) {
          case 'y':
            return padding(date.getFullYear(), $0.length)
          case 'M':
            return padding(date.getMonth() + 1, $0.length)
          case 'd':
            return padding(date.getDate(), $0.length)
          case 'w':
            return date.getDay() + 1
          case 'h':
            return padding(date.getHours(), $0.length)
          case 'm':
            return padding(date.getMinutes(), $0.length)
          case 's':
            return padding(date.getSeconds(), $0.length)
        }
      })
    },
    parse(dateString, pattern) {
      let matchs1 = pattern.match(SIGN_REGEXP)
      let matchs2 = dateString.match(/(\d)+/g)
      if (matchs1.length === matchs2.length) {
        var _date = new Date(1970, 0, 1)
        for (var i = 0; i < matchs1.length; i++) {
          var _int = parseInt(matchs2[i])
          var sign = matchs1[i]
          switch (sign.charAt(0)) {
            case 'y':
              _date.setFullYear(_int)
              break
            case 'M':
              _date.setMonth(_int - 1)
              break
            case 'd':
              _date.setDate(_int)
              break
            case 'h':
              _date.setHours(_int)
              break
            case 'm':
              _date.setMinutes(_int)
              break
            case 's':
              _date.setSeconds(_int)
              break
          }
        }
        return _date
      }
      return null
    },
    getCurrentDateStr(pattern = 'yyyy-MM-dd') {
      return this.formatDate(new Date(), pattern)
    },
    isInArray(value, arr) {
      for (let i = 0; i < arr.length; i++) {
        if (value === arr[i]) {
          return true
        }
      }
      return false
    },
    getCodeName(codetype, code, hideCode) {
      if (codetype) {
        let ldcodes = this.$store.getters.ldcodes
        let arrays = ldcodes[codetype]
        if (!arrays) {
          return ''
        }
        if (code !== '' && code !== undefined) {
          if (code instanceof Array && code.length > 0) {
            let newData = []
            arrays.forEach(data => {
              if (code.includes(data.code)) {
                if (true === hideCode) {
                  newData.push(data.codename)
                } else {
                  newData.push(data.code + ':' + data.codename)
                }
              }
            })
            return newData.join(', ')
          } else {
            // 转为字符串类型
            code = String(code)
            if (code.startsWith('[') && code.endsWith(']')) {
              code = JSON.parse(code)
              return this.getCodeName(codetype, code, hideCode)
            }
            const result = arrays.filter(data => {
              return code === data.code
            })
            if (result instanceof Array && result.length === 1) {
              if (true === hideCode) {
                return result[0].codename
              } else {
                return result[0].code + ':' + result[0].codename
              }
            }
            return ''
          }
        }
        return ''
      }
    },
    getCascaderCodeName(codetype, codeArray, hideCode) {
      if (typeof codeArray === 'string' && codeArray.startsWith('[') && codeArray.endsWith(']')) {
        codeArray = JSON.parse(codeArray)
        return this.getCascaderCodeName(codetype, codeArray, hideCode)
      } else if (codeArray[0] instanceof Array) {
        let codeName = []
        for (const codeArrayElement of codeArray) {
          codeName.push(this.getCascaderCodeName(codetype, codeArrayElement, hideCode))
        }
        return codeName.join(', ')
      } else {
        const newCodeType = codetype + '.' + codeArray[0]
        if (true === hideCode) {
          return this.getCodeName(newCodeType, codeArray, hideCode)
        } else {
          return codeArray[0] + '_' + this.getCodeName(newCodeType, codeArray, hideCode)
        }
      }
    },
    updateOrderIndexArr(_orderIndexArr, orderIndexArrName) {
      this[orderIndexArrName] = _orderIndexArr
    },
    export2Excel(columns, list, fileName, autoWidth, bookType) {
      require.ensure([], () => {
        const {export_json_to_excel} = require('@/vendor/Export2Excel')
        let tHeader = []
        let filterVal = []
        if (!columns) {
          return
        }
        columns.forEach(item => {
          tHeader.push(item.title)
          filterVal.push(item.key)
        })
        const data = list.map(v => filterVal.map(j => String(v[j])))
        export_json_to_excel({
          header: tHeader,
          data: data,
          filename: fileName,
          autoWidth: autoWidth,
          bookType: bookType
        })
      })
    },
    uploadUtil(files) {
      let formData = new FormData()
      if (files.length) {
        files.forEach(file => {
          formData.append('file', file.raw)
        })
      } else if (files.raw) {
        formData.append('file', files.raw)
      }
      return formData
    },
    initTableIndex(page, index) {
      return (this.$refs[page].page - 1) * this.$refs[page].pageSize_ + index + 1
    },
    isInAllVue(path) {
      if (allVue.keys().some(url => url.indexOf(path) > 0)) {
        return path
      } else {
        //  basicinfo/productAttributeDefinition/hsbc/index
        let index1 = path.lastIndexOf('/')
        let index2 = path.lastIndexOf('/', index1 - 1)
        path = path.substring(0, index2) + path.substring(index1)
        return path
      }
    },
    changeCurrentNavTitle(name) {
      let elements = document.getElementsByClassName('tags-view-item active')
      if (elements.length > 0) {
        let ele = elements[0]
        ele.childNodes[0].nodeValue = name
      }
    },
    filters_method(value, row, column) {
      const property = column['property']
      return row[property] === value
    },
    onlyDisabeldTableButton(){
      let dialogOnly = this.getStrData("dialogOnly")
      if("true"=== dialogOnly){
        let tags = document.getElementsByClassName('dialog-disable-button');
        for(let i = 0;i < tags.length; i++){
          let target = tags[i];
          let dialogDisable = target.getAttribute('dialog-disable')
          if (dialogDisable !== 'disable') {
            continue
          }
          target.disabled = true
          target.classList.add('is-disabled')
        }
      }
    },
    onlyQueryDisabledButton() {
      let dialogOnly = this.getStrData("dialogOnly")
      if ("true" === dialogOnly) {
        let tagNames = document.getElementsByTagName('button')
        let startIndex = this._.findIndex(tagNames, function (o) {
          return o._prevClass === 'el-dialog__headerbtn'
        })
        //获取dialog下的button
        let dialogButtons = this._.slice(tagNames, startIndex)
        let classSmallButtons = this._.filter(dialogButtons, function (d) {
          return d.className.indexOf('el-button') >= 0
        })
        for (let i = 0; i < classSmallButtons.length; i++) {
          let target = classSmallButtons[i]
          let dialogDisable = target.getAttribute('dialog-disable')
          if (dialogDisable !== 'disable') {
            continue
          }
          target.disabled = true
          target.classList.add('is-disabled')
        }
      }
    },
    filterProductByNode(that, pdNode = '0', diffUser = false) {
      // 1-产品框架定义  2-产品营运 3-产品测试 4-产品审核 5-产品发布 6-产品停售 7-产品规则订正 8-产品公式订正
      let list = this.filterDataByNode(this.$store.getters.products,pdNode,diffUser)
      that.productOriginalList = list
      that.productList = list
      this.filterEditionByProduct(that)
      return list
    },
    filterProductByCategory(that, pdCategory) {
      that.selectedPd = ''
      if (pdCategory) {
        that.productList = this._.filter(that.productOriginalList, {'pdCategory': pdCategory})
      }
      this.filterEditionByProduct(that)
    },
    filterEditionByProduct(that, product) {
      that.params.pdCode = ''
      that.params.pdName = ''
      that.params.pdEdition = ''
      that.pdEditionList = []
      if (product) {
        that.params.pdCode = product.pdCode
        that.params.pdName = product.pdName
        const productListFilter = this._.filter(that.productList, {'pdCode': product.pdCode}, {'pdName': product.pdName})
        that.pdEditionList = this._.map(productListFilter, 'pdEdition')
      }
    },
    filterPackageByNode(that, pdNode = '0', diffUser = false) {
      let package_list = this.filterDataByNode(this.$store.getters.packages,pdNode,diffUser)
      that.packageList = package_list
      return package_list
    },
    filterDataByNode(data,pdNode,diffUser){
      let list = []
      if (pdNode !== '0') {
        list = this._.filter(data, {'pdNode': pdNode})
      } else {
        list = data
      }
      if (diffUser) {
        let userCode = this.$store.getters.usercode
        list = this._.filter(list, {'assignName': userCode})
      }
      return list
    }
  }
}

function padding(s, len) {
  len = len - (s + '').length
  for (let i = 0; i < len; i++) {
    s = '0' + s
  }
  return s
}
